//SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.3;

import "./BullaGroup.sol";
import "./BullaManager.sol";

contract BullaClaim {
    enum ActionType {Payment, Reject, Rescind}
    enum Status {Pending, Repaying, Paid, Rejected, Rescinded}
    enum RejectReason {
        None,
        UnknownAddress,
        DisputedClaim,
        SuspectedFraud,
        Other
    }

    //structure for storing IPFS hash that may hold documents
    struct Multihash {
        bytes32 hash;
        uint8 hashFunction;
        uint8 size;
    }
    Multihash public multihash;

    uint256 public bullaId; //parent bullaId
    address public bullaGroup; //parent bullaGroup

    address payable public owner; //current owner of claim
    address payable public creditor;
    address payable public debtor;

    uint256 public claimAmount;
    uint256 public dueBy;
    uint256 public paidAmount;
    Status public status;

    //current price that owner is willing to transfer claim
    uint256 public transferPrice;

    modifier onlyCreditor() {
        require(creditor == msg.sender, "restricted to creditor wallet");
        _;
    }

    modifier onlyDebtor() {
        require(debtor == msg.sender, "restricted to debtor wallet");
        _;
    }

    modifier onlyOwner() {
        require(owner == msg.sender, "restricted to owning wallet");
        _;
    }

    event ClaimAction(
        address indexed bullaManager,
        address indexed bullaGroup,
        uint256 indexed bullaId,
        address bullaClaim,
        ActionType actionType,
        uint256 paymentAmount,
        RejectReason rejectReason,
        uint256 blocktime
    );

    event FeePaid(
        address indexed bullaManager,
        address indexed bullaClaim,
        address indexed collectionAddress,
        uint256 transactionFee,
        uint256 blocktime
    );

    event MultihashAdded(
        address indexed bullaManager,
        address bullaClaim,
        Multihash ipfsHash,
        uint256 blocktime
    );

    event TransferPriceUpdated(
        address indexed bullaClaim,
        uint256 oldPrice,
        uint256 newPrice,
        uint256 blocktime
    );

    event ClaimTransferred(
        address indexed bullaClaim,
        address indexed oldOwner,
        address indexed newOwner,
        uint256 trasferPrice,
        uint256 blocktime
    );

    constructor(
        uint256 _bullaId,
        address payable _owner,
        address payable _creditor,
        address payable _debtor,
        uint256 _claimAmount,
        uint256 _dueBy
    ) {
        bullaGroup = msg.sender;
        bullaId = _bullaId;
        owner = _owner;
        creditor = _creditor;
        debtor = _debtor;
        claimAmount = _claimAmount;
        dueBy = _dueBy;
    }

    function setTransferPrice(uint256 newPrice) external onlyOwner {
        require(owner == creditor, "only owner can set price");
        uint256 oldPrice = transferPrice;
        transferPrice = newPrice;
        emit TransferPriceUpdated(
            address(this),
            oldPrice,
            newPrice,
            block.timestamp
        );
    }

    function transferOwnership(address payable newOwner) external payable {
        require(owner == creditor, "only invoices can be transferred");
        require(
            transferPrice > 0 || msg.sender == owner,
            "this claim is not transferable by anyone other than owner"
        );
        require(
            msg.value == transferPrice,
            "incorrect msg.value to transfer ownership"
        );

        //TODO: is this done in the correct order. Is it safe?
        owner.transfer(msg.value);
        address oldOwner = owner;
        owner = newOwner;
        creditor = newOwner;
        transferPrice = 0;

        emit ClaimTransferred(
            address(this),
            oldOwner,
            newOwner,
            msg.value,
            block.timestamp
        );
    }

    function addMultihash(
        bytes32 hash,
        uint8 hashFunction,
        uint8 size
    ) external onlyOwner {
        multihash = Multihash(hash, hashFunction, size);
        emit MultihashAdded(
            getBullaManager(),
            address(this),
            multihash,
            block.timestamp
        );
    }

    function getBullaManager() internal view returns (address) {
        BullaGroup _bullaGroup = BullaGroup(bullaGroup);
        return _bullaGroup.bullaManager();
    }

    function getFeeInfo() public view returns (uint256, address payable) {
        BullaManager bullaManager = BullaManager(getBullaManager());
        uint256 bullaTokenBalance = bullaManager.getBullaBalance(owner);
        (
            address payable collectionAddress,
            uint32 fullFee,
            uint32 bullaThreshold,
            uint32 reducedFeeBasisPoints
        ) = bullaManager.feeInfo();

        uint32 fee =
            bullaThreshold > 0 && bullaTokenBalance >= bullaThreshold
                ? reducedFeeBasisPoints
                : fullFee;
        return (fee, collectionAddress);
    }

    function calculateFee(uint256 bpFee, uint256 value)
        internal
        pure
        returns (uint256)
    {
        return (value * bpFee) / 10000;
    }

    function emitActionEvent(
        ActionType actionType,
        uint256 _paymentAmount,
        RejectReason reason
    ) internal {
        emit ClaimAction(
            getBullaManager(),
            bullaGroup,
            bullaId,
            address(this),
            actionType,
            _paymentAmount,
            reason,
            block.timestamp
        );
    }

    function payClaim() external payable onlyDebtor {
        require(paidAmount + msg.value <= claimAmount, "repaying too much");
        require(msg.value > 0, "payment must be greater than 0");

        (uint256 feeBasisPoints, address payable collectionAddress) =
            getFeeInfo();

        uint256 transactionFee =
            feeBasisPoints > 0 ? calculateFee(feeBasisPoints, msg.value) : 0;
        address bullaManager = getBullaManager();

        creditor.transfer(msg.value - transactionFee);
        emitActionEvent(ActionType.Payment, claimAmount, RejectReason.None);
        paidAmount += msg.value;
        paidAmount == claimAmount ? status = Status.Paid : status = Status
            .Repaying;

        if (transactionFee > 0) {
            collectionAddress.transfer(transactionFee);
        }
        emit FeePaid(
            bullaManager,
            address(this),
            collectionAddress,
            transactionFee,
            block.timestamp
        );
    }

    function rejectClaim(RejectReason reason) external payable onlyDebtor {
        require(
            status == Status.Pending,
            "cannot reject once payment has been made"
        );
        status = Status.Rejected;
        emitActionEvent(ActionType.Reject, 0, reason);
    }

    function rescindClaim() external payable onlyCreditor {
        require(
            status == Status.Pending,
            "cannot rescind once payment has been made"
        );
        status = Status.Rescinded;
        emitActionEvent(ActionType.Rescind, 0, RejectReason.None);
    }
}

contracts/BullaGroup.sol.content:
//SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.3;

import "./BullaClaim.sol";

contract BullaGroup {
    mapping(uint256 => address) public bullaOwners;
    uint256 bullaCount = 0;

    mapping(address => bool) public isMember;
    bool public immutable requireMembership;

    bytes32 public immutable groupType;
    address public immutable bullaManager;
    address public immutable owner;

    event NewBulla(
        address indexed bullaManager,
        address indexed bullaGroup,
        uint256 bullaId,
        address indexed owner,
        string description,
        uint256 ownerFunding,
        uint256 blocktime
    );

    event NewBullaClaim(
        address indexed bullaManager,
        address bullaGroup,
        uint256 bullaId,
        address bullaClaim,
        address owner,
        address indexed creditor,
        address indexed debtor,
        string description,
        uint256 claimAmount,
        uint256 dueBy,
        uint256 blocktime
    );

    event Membership(
        address indexed groupAddress,
        address walletAddress,
        bool isMember,
        uint256 blocktime
    );

    constructor(
        address _bullaManager,
        address _owner,
        bytes32 _groupType,
        bool _requireMembership
    ) {
        owner = _owner;
        bullaManager = _bullaManager;
        isMember[_owner] = true;
        requireMembership = _requireMembership;
        groupType = _groupType;
    }

    function joinGroup() external {
        require(isMember[msg.sender] != true, "members cannot join a group");
        isMember[msg.sender] = true;
        emit Membership(address(this), msg.sender, true, block.timestamp);
    }

    function leaveGroup() external {
        require(
            isMember[msg.sender] == true,
            "non-members cannot leave a group"
        );
        require(msg.sender != owner, "owners cannot leave a group");
        isMember[msg.sender] = false;
        emit Membership(address(this), msg.sender, false, block.timestamp);
    }

    function createBulla(string calldata desc, uint256 ownerFunding) external {
        if (requireMembership)
            require(
                isMember[msg.sender] == true,
                "non-members cannot make journal"
            );
        uint256 newBullaId = bullaCount;

        //bullas[newBullaId].id = newBullaId;
        bullaOwners[newBullaId] = msg.sender;
        bullaCount++;

        emit NewBulla(
            bullaManager,
            address(this),
            newBullaId,
            msg.sender,
            desc,
            ownerFunding,
            block.timestamp
        );
    }

    function createBullaClaim(
        uint256 bullaId,
        uint256 claimAmount,
        address payable creditor,
        address payable debtor,
        string memory description,
        uint256 dueBy
    ) public {
        require(bullaOwners[bullaId] != address(0), "bulla does not exist");
        require(
            bullaOwners[bullaId] == msg.sender,
            "only bulla owner's may create a bulla claim"
        );

        BullaClaim newBullaClaim =
            new BullaClaim(
                bullaId,
                payable(msg.sender),
                creditor,
                debtor,
                claimAmount,
                dueBy
            );

        emit NewBullaClaim(
            bullaManager,
            address(this),
            bullaId,
            address(newBullaClaim),
            msg.sender,
            creditor,
            debtor,
            description,
            claimAmount,
            dueBy,
            block.timestamp
        );
    }
}

contracts/BullaManager.sol.content:
//SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.3;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./BullaGroup.sol";

struct FeeInfo {
    address payable collectionAddress;
    uint32 feeBasisPoints;
    uint32 bullaThreshold; //# of BULLA tokens held to get fee reduction
    uint32 reducedFeeBasisPoints; //reduced fee for BULLA token holders
}

contract BullaManager {
    bytes32 public immutable description;
    FeeInfo public feeInfo;
    IERC20 public bullaToken;
    address public owner;
    modifier onlyOwner() {
        require(owner == msg.sender, "restricted to contract owner");
        _;
    }

    event NewBullaGroup(
        address indexed bullaManager,
        address indexed bullaGroup,
        address owner,
        string description,
        bytes32 groupType,
        bool requireMembership,
        uint256 blocktime
    );
    event FeeChanged(
        address indexed bullaManager,
        uint256 prevFee,
        uint256 newFee,
        uint256 blocktime
    );
    event CollectorChanged(
        address indexed bullaManager,
        address prevCollector,
        address newCollector,
        uint256 blocktime
    );
    event OwnerChanged(
        address indexed bullaManager,
        address prevOwner,
        address newOwner,
        uint256 blocktime
    );
    event BullaTokenChanged(
        address indexed bullaManager,
        address prevBullaToken,
        address newBullaToken,
        uint256 blocktime
    );
    event FeeThresholdChanged(
        address indexed bullaManager,
        uint256 prevFeeThreshold,
        uint256 newFeeThreshold,
        uint256 blocktime
    );
    event ReducedFeeChanged(
        address indexed bullaManager,
        uint256 prevFee,
        uint256 newFee,
        uint256 blocktime
    );

    constructor(
        bytes32 _description,
        address payable _collectionAddress,
        uint32 _feeBasisPoints
    ) {
        owner = msg.sender;
        feeInfo.collectionAddress = _collectionAddress;
        description = _description;
        feeInfo.feeBasisPoints = _feeBasisPoints;

        emit FeeChanged(address(this), 0, _feeBasisPoints, block.timestamp);
        emit CollectorChanged(
            address(this),
            address(0),
            _collectionAddress,
            block.timestamp
        );
        emit OwnerChanged(
            address(this),
            address(0),
            msg.sender,
            block.timestamp
        );
    }

    function createBullaGroup(
        string calldata _description,
        bytes32 groupType,
        bool requireMembership
    ) external {
        BullaGroup newGroup =
            new BullaGroup(
                address(this),
                msg.sender,
                groupType,
                requireMembership
            );
        emit NewBullaGroup(
            address(this),
            address(newGroup),
            msg.sender,
            _description,
            groupType,
            requireMembership,
            block.timestamp
        );
    }

    function setOwner(address _owner) external onlyOwner {
        owner = _owner;
        emit OwnerChanged(address(this), owner, _owner, block.timestamp);
    }

    function setFee(uint32 _feeBasisPoints) external onlyOwner {
        uint32 oldFee = feeInfo.feeBasisPoints;
        feeInfo.feeBasisPoints = _feeBasisPoints;
        emit FeeChanged(
            address(this),
            oldFee,
            feeInfo.feeBasisPoints,
            block.timestamp
        );
    }

    function setCollectionAddress(address payable _collectionAddress)
        external
        onlyOwner
    {
        feeInfo.collectionAddress = _collectionAddress;
        emit CollectorChanged(
            address(this),
            feeInfo.collectionAddress,
            _collectionAddress,
            block.timestamp
        );
    }

    //Set threshold of BULLA tokens owned that are required to receive reduced fee
    function setbullaThreshold(uint32 _threshold) external onlyOwner {
        feeInfo.bullaThreshold = _threshold;
        emit FeeThresholdChanged(
            address(this),
            feeInfo.bullaThreshold,
            _threshold,
            block.timestamp
        );
    }

    //reduced fee if threshold of BULLA tokens owned is met
    function setReducedFee(uint32 reducedFeeBasisPoints) external onlyOwner {
        uint32 oldFee = feeInfo.reducedFeeBasisPoints;
        feeInfo.reducedFeeBasisPoints = reducedFeeBasisPoints;
        emit FeeChanged(
            address(this),
            oldFee,
            feeInfo.feeBasisPoints,
            block.timestamp
        );
    }

    //set the contract address of BULLA ERC20 token
    function setBullaTokenAddress(address payable _bullaTokenAddress)
        external
        onlyOwner
    {
        bullaToken = IERC20(_bullaTokenAddress);
        emit BullaTokenChanged(
            address(this),
            address(bullaToken),
            _bullaTokenAddress,
            block.timestamp
        );
    }

    //get the amount of BULLA tokens held by a given address
    function getBullaBalance(address _holder) external view returns (uint256) {
        uint256 balance =
            address(bullaToken) == address(0)
                ? 0
                : bullaToken.balanceOf(_holder);
        return balance;
    }
}

